var express = require('express'),
    puer    = require('puer'),
    http    = require('http'),
    path    = require('path');

var restful = require('./restful');

var app = express();

// all environments
app.set('port', process.env.PORT || 2013);

app.set('views', __dirname + '/../templates');
app.set('view engine', 'mu');
app.engine('mu', require('./mustache').__express);

app.use(express.compress());
app.use(express.favicon());
app.use(express.logger('dev'));
app.use(express.bodyParser());
app.use(express.methodOverride());

// f5
app.use(puer.connect(app, {
        dir: [__dirname],
        interval: 500, // 监听文件的间隔,同上面的 -t --time参数
        ignored: /(\/|^)\..*|node_modules|.*\.less$/  //忽略的监听文件，默认忽略dotfile 和 node_modules
    }
))

app.use(app.router);
app.use(express.static(path.join(__dirname, '../', 'www')));

// development only
if ('development' == app.get('env')) {
    app.use(express.errorHandler());
}
restful(app);
http.createServer(app).listen(app.get('port'), function(){
    console.log('Express server listening on port ' + app.get('port'));
});
